var devices = msg.payload;
var deviceList = [];

// Itera sobre os dispositivos e extrai as informações necessárias
for (var key in devices) {
    if (devices.hasOwnProperty(key)) {
        var device = devices[key];
        if(device.friendly_name != 'Coordinator'){
            deviceList.push({
            ieeeAddress: device.ieee_address,
            friendlyName: device.friendly_name
        });
        }        
    }
}

// Guarda lista dos dispositivos na variavel global
global.set("devsList", deviceList);

// Configura a mensagem para o nó debug
msg.payload = deviceList;
return msg;

var json = JSON.parse(msg.payload);

if(json.id && json.state){
    // Processar TOGGLE
    msg.topic = 'zigbee2mqtt/' + json.id + '/set';
    msg.payload = json.state;
    return [msg, null];

} else if(json.topico && json.carga) {
    // Processar RENAME
    msg.topic = json.topico;
    msg.payload = json.carga; 
    return [msg, null];   
}
else if (json.getdevstate ) {
    // Processar GET_DEVS_STATE

    // Obter dados da variavel global
    const listaDevs = global.get("devsList");
    var messages = [];

    // Loop através da lista de tópicos
    for (var i = 0; i < listaDevs.length; i++) {
        messages.push({
            topic: 'zigbee2mqtt/' + listaDevs[i].ieeeAddress + '/get',
            payload: '{"state":""}'
        });
    }

    // Define a lista de mensagens como payload
    return [[messages], null];
}
else if (json.cmdtimer) {
    msg.payload = {
        command: json.command,
        name: json.name,
        topic: json.topic,
        expression: json.expression,
        payload: json.payload,
        payloadType: json.payloadType
    };

    return[null, msg]
}

