const topico = msg.topic;

if(msg.payload.state != 'TOGGLE' && !topico.includes('bridge') && !topico.includes('Cordinator') && !topico.includes('get')){
    // Obter nome do device
    const parts = (msg.topic).split('/');
    const deviceName = parts[1];
    const get_estado = msg.payload.state;
    msg.payload = deviceName;

    // Obter ID do device
    const device = listaDevs.find(d => d.friendlyName === deviceName);
    if (device) {
        msg.payload = { Id: device.ieeeAddress, Nome:deviceName ,Estado: get_estado };
    } else {
        return;
    }   
}
else{
    return;
}

return msg;

