msg.topic = null;
msg.payload = {"command": "status-all" }

node.send([null, msg]);

return null;

if (msg.payload.config) {
    // Extrair topic e payload do objeto disparado pelo cron-plus
    let topic = msg.payload.config.topic;
    let payload = msg.payload.config.payload;

    // Formatando a mensagem MQTT
    msg.topic = topic;
    msg.payload = payload;  

    return msg;
}

if(!msg.payload.result){
    return null;
}

// Se o Timer selecionado nao esta configurado, retornar null
if (!msg.payload.result.config){
    msg.topic = 'GET_TIMER';
    msg.payload = null;
    return msg;
}
else if (Array.isArray(msg.payload.result)) {
    // Obtem o array result do JSON de entrada
    let results = msg.payload.result;

    // Cria um array para armazenar as mensagens de saída
    let output = [];

    // Percorre cada objeto no array result
    for (let i = 0; i < results.length; i++) {
        let config = results[i].config;

        // Extrai os valores de name, payload e expression
        let name = config.name;
        let payload = config.payload;
        let expression = config.expression;
        let isrunning = config.isRunning;

        // Cria uma nova mensagem com esses valores
        let newMsg = {
            name: name,
            payload: payload,
            expression: expression,
            isrunning: isrunning
        };

        // Adiciona ao array de saída
        output.push(newMsg);
    }

    // Retorna a lista de mensagens
    msg.topic = 'GET_ALL_TIMERS';
    msg.payload = output;
    return msg;

}
else if (typeof msg.payload.result === 'object'){
    // Enviar dados do Timer, configurado, seleccionado
    //let timer = msg.payload.result.config.name;
    let accao = JSON.parse(msg.payload.result.config.payload);
    let activo = msg.payload.result.config.isRunning;
    let hora_weekdays = msg.payload.result.config.expression;

    var objecto = {
        Accao: accao.state,
        Activo: activo,
        HoraWeekDays: hora_weekdays
    };

    msg.topic = 'GET_DEV_TIMER';
    msg.payload = JSON.stringify(objecto);

    return msg;
}

