var json = JSON.parse(msg.payload);

if (json.id && json.state) {  // Processar TOGGLE  

    // Separa o ultimo caracter adicionadao no ieeeAddress
    let texto = json.id;
    let lastchar = texto[texto.length - 1];
    let ieeaddr = texto.slice(0, -1);

    // Crair Topico par TOGGLE
    msg.topic = `zigbee2mqtt/${ieeaddr}/${lastchar}/set`;
    msg.payload = json.state;
    return [msg, null];

} else if (json.topico && json.carga) { // Processar RENAME

    msg.topic = json.topico;
    msg.payload = json.carga; 
    return [msg, null];   
}
else if (json.getdevstate) { // Processar GET_DEVS_STATE

    // Obter dados da variavel global
    const listaDevs = global.get("devsList");
    var messages = [];

    // Loop através da lista de tópicos
    for (var i = 0; i < listaDevs.length; i++) {
        messages.push({
            topic: 'zigbee2mqtt/' + listaDevs[i].ieeeAddress + '/get',
            payload: '{"state":""}'
        });
    }

    // Define a lista de mensagens como payload
    return [[messages], null];
}
else if (json.cmdtimer == 'CONFIG_TIMER') { // Processar CONFIG_TIMER
    msg.payload = {
        command: json.command,
        name: json.name,
        topic: json.topic,
        expression: json.expression,
        payload: json.payload,
        payloadType: json.payloadType
    };

    return[null, msg]
}
else if (json.cmdtimer == 'GET_TIMER') { // Processar GET_TIMER
    msg.payload = {
        command: json.command,
        name: json.name
    };

    return [null, msg]
}
else if (json.cmdtimer == 'TIMER_STATE') {  // Processar TIMER_STATE
    msg.payload = {
        command: json.command,
        name: json.name
    };

    return [null, msg]
}

