else if (Array.isArray(msg.payload.result)) {
    // Obtem o array result do JSON de entrada
    let results = msg.payload.result;

    // Cria um array para armazenar as mensagens de saída
    let output = [];

    // Percorre cada objeto no array result
    for (let i = 0; i < results.length; i++) {
        let config = results[i].config;

        // Extrai os valores de name, payload e expression
        let name = config.name;
        let payload = config.payload;
        let expression = config.expression;
        let isrunning = config.isRunning;

        // Cria uma nova mensagem com esses valores
        let newMsg = {
            name: name,
            payload: payload,
            expression: expression,
            isrunning: isrunning
        };

        // Adiciona ao array de saída
        output.push(newMsg);
    }

    // Retorna a lista de mensagens
    msg.topic = 'GET_ALL_TIMERS';
    msg.payload = output;
    return msg;

}
